import Vue from "vue";
import VueRouter from "vue-router";
import CreateShortLink from "../views/CreateShortLink.vue";
import Layout from "@/views/Layout.vue";
import LinkDetail from "@/views/LinkDetail.vue";
import ShortLinkList from "@/views/ShortLinkList.vue";
import Login from "@/views/Login.vue";
import User from "@/views/User.vue";
import Tag from "@/views/TagManage.vue"

Vue.use(VueRouter);

const routes = [
  {
    path: "/login",
    name: "Login",
    component: Login,
  },

  {
    path: "/",
    name: "Layout",
    component: Layout,
    redirect: "/create",

    children: [
      {
        path: "create",
        name: "CreateShortLink",
        component: CreateShortLink,
      },
      {
        path: "list",
        name: "ShortLinkList",
        component: ShortLinkList,
      },
      {
        path: "detail",
        name: "LinkDetail",
        component: LinkDetail,
      },
      {
        path: "user",
        name: "User",
        component: User,
      },
      {
        path: "Tag",
        name: "Tag",
        component: Tag,
      },
    ],
  },
];

const router = new VueRouter({
  mode: "history",
  base: process.env.BASE_URL,
  routes,
});

